{
    "openapi": "3.0.3",
    "info": {
        "title": "Village Wallet",
        "description": "APIs for Village Wallet",
        "version": "0.0.7"
    },
    "servers": [
        {
            "url": "http://localhost:3000",
            "description": "Local server"
        },
        {
            "url": "https://dev.mobile-api.woolworths.com.au/wow/v1/dpwallet",
            "description": "Development server - DP DEV 4"
        }
    ],
    "components": {
        "schemas": {
            "error": {
                "description": "The details of an error that has occurred",
                "required": [
                    "httpStatusCode",
                    "errorCode",
                    "errorMessage",
                    "errorDetail"
                ],
                "type": "object",
                "properties": {
                    "httpStatusCode": {
                        "description": "The http status code (4xx and 5xx HTTP status codes)",
                        "type": "number",
                        "example": 500
                    },
                    "errorCode": {
                        "description": "The error code",
                        "type": "string",
                        "example": "AP99"
                    },
                    "errorMessage": {
                        "description": "The error message",
                        "type": "string",
                        "example": "Internal Server Error"
                    },
                    "errorDetail": {
                        "description": "The error detail",
                        "type": "string",
                        "example": "Oops! An error has occurred: ScriptExecutionFailed"
                    }
                },
                "example": {
                    "httpStatusCode": 500,
                    "errorCode": "AP99",
                    "errorMessage": "Internal Server Error",
                    "errorDetail": "Oops! An error has occurred: ScriptExecutionFailed"
                }
            },
            "meta": {
                "description": "Object to contain any metadata",
                "type": "object",
                "additionalProperties": true
            },
            "metaPaginated": {
                "description": "Meta Object containing pagination information",
                "type": "object",
                "required": [
                    "recordCount",
                    "totalRecords"
                ],
                "properties": {
                    "recordCount": {
                        "description": "The number of records returned in the result set",
                        "type": "integer",
                        "minimum": 0
                    },
                    "totalRecords": {
                        "description": "The number of records in total in the full query set",
                        "type": "integer",
                        "minimum": 0
                    }
                }
            },
            "metaChallenge": {
                "description": "Meta Object containing responses to a step up challenges",
                "type": "object",
                "properties": {
                    "challengeResponses": {
                        "description": "The collection of challenges",
                        "type": "array",
                        "items": {
                            "description": "The response to a step up challenge (e.g. credit card step up or gift card passcode)",
                            "type": "object",
                            "required": [
                                "instrumentId",
                                "type",
                                "token"
                            ],
                            "properties": {
                                "instrumentId": {
                                    "description": "The instrument id related to the step up challenge",
                                    "type": "string"
                                },
                                "type": {
                                    "description": "The type of token that has been provided",
                                    "type": "string",
                                    "enum": [
                                        "STEP_UP",
                                        "PASSCODE"
                                    ]
                                },
                                "token": {
                                    "description": "The value requested by the challenge",
                                    "type": "string"
                                },
                                "reference" : {
                                    "description": "An optional reference that could be used for audit tracing",
                                    "type": "string"
                                }
                            }
                        }
                    }
                }
            },
            "preferenceGroup": {
                "description": "A set of groups of key/value based preferences",
                "type": "object",
                "additionalProperties": {
                    "description": "A group or category of preferences",
                    "type": "object",
                    "additionalProperties": {
                        "description": "A single preference setting",
                        "type": "string"
                    }
                }
            },
            "qr": {
                "description": "Detail of a QR code",
                "type": "object",
                "required": [
                    "qrId",
                    "referenceId",
                    "referenceType",
                    "content",
                    "image"
                ],
                "properties": {
                    "qrId": {
                        "description": "The ID of the QR code",
                        "type": "string"
                    },
                    "referenceId": {
                        "description": "The ID of the payment request linked to this QR code",
                        "type": "string"
                    },
                    "referenceType": {
                        "description": "The type of ID held in referenceId",
                        "type": "string",
                        "enum": [
                            "PAYMENT_REQUEST",
                            "PAYMENT_SESSION"
                        ],
                        "example": "PAYMENT_REQUEST"
                    },
                    "content": {
                        "description": "The text content for the QR code.  The template used in creating this value can be overriden using merchant preferences.",
                        "type": "string"
                    },
                    "image": {
                        "description": "Base64 encoded PNG of the QR Code",
                        "type": "string"
                    },
                    "expiryTime": {
                        "description": "ISO date/time string indicating when the QR code will expire and become ineffective.  If absent then the QR code will not expire until it is deleted",
                        "type": "string",
                        "format": "date-time",
                        "example": "2017-11-06T19:38:09.890+11:00"
                    }
                }
            },
            "basket": {
                "description": "Information provided by the Merchant on the basket of items associated with the payment request.  This payload originates in the payment request and is carried with any resulting transactions",
                "type": "object",
                "properties": {
                    "items": {
                        "description": "An array of items in the basket",
                        "type": "array",
                        "items": {
                            "required": [
                                "label"
                            ],
                            "type": "object",
                            "properties": {
                                "label": {
                                    "description": "Short label for the basket item",
                                    "type": "string"
                                },
                                "description": {
                                    "description": "Longer description of the basket item",
                                    "type": "string"
                                },
                                "quantity": {
                                    "description": "The number of units of the item in the basket if multiple is possible.  Is a number to accommodate items charged by weight or length",
                                    "type": "number"
                                },
                                "unitPrice": {
                                    "description": "The unit price of the item.  May be positive or negative",
                                    "type": "number"
                                },
                                "unitMeasure": {
                                    "description": "Optional display string for the measure of the unit",
                                    "type": "string"
                                },
                                "totalPrice": {
                                    "description": "The total price of the item.  May be positive or negative",
                                    "type": "number"
                                },
                                "tags": {
                                    "description": "Additional string/string key/value pairs for the item defined by the merchant",
                                    "type": "object",
                                    "additionalProperties": {
                                        "type": "string"
                                    }
                                }
                            }
                        }
                    }
                }
            },
            "posPayload": {
                "description": "Payload describing the specific POS system.  This payload originates in the payment request and is carried with any resulting transactions.  Provided in a generic schema previous configured by the merchant",
                "type": "object",
                "properties": {
                    "schemaId": {
                        "description": "The ID of the previously configured schema that will be used to validate the contents of the payload",
                        "type": "string"
                    },
                    "payload": {
                        "description": "The ID of the previously configured schema that will be used to validate the contents of the payload",
                        "type": "object",
                        "additionalProperties": true
                    }
                }
            },
            "merchantPayload": {
                "description": "Payload provided by the merchant to support other types of messaging.  This payload originates in the payment request and is carried with any resulting transactions.  Provided in a generic schema previous configured by the merchant",
                "type": "object",
                "properties": {
                    "schemaId": {
                        "description": "The ID of the previously configured schema that will be used to validate the contents of the payload",
                        "type": "string"
                    },
                    "payload": {
                        "description": "The ID of the previously configured schema that will be used to validate the contents of the payload",
                        "type": "object",
                        "additionalProperties": true
                    }
                }
            },
            "commonPaymentBase": {
                "type": "object",
                "required": [
                    "paymentRequestId",
                    "merchantReferenceId",
                    "grossAmount"
                ],
                "properties": {
                    "paymentRequestId": {
                        "description": "The ID of this payment request",
                        "type": "string",
                        "example": "75ba5b0b-7e5d-47fe-9508-29ca69fdb1d5"
                    },
                    "merchantReferenceId": {
                        "description": "The unique reference for the payment as defined by the Merchant",
                        "type": "string",
                        "example": "75ba5b0b-7e5d-47fe-9508-29ca69fdb1d5"
                    },
                    "grossAmount": {
                        "description": "The gross amount to be paid.  Must be positive except for refunds",
                        "type": "number"
                    }
                }
            },
            "merchantPaymentSummary": {
                "description": "Summary information for a single payment request",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/commonPaymentBase"
                    },
                    {
                        "type": "object",
                        "properties": {
                            "usesRemaining": {
                                "description": "The number of times that the payment request can be used to create a payment. If absent then request can be used an unlimited number of times",
                                "type": "integer",
                                "minimum": 0
                            },
                            "expiryTime": {
                                "description": "The ISO date/time for when the payment request will expire and become unusable for payments.  If absent then the payment request will not expire until it is deleted",
                                "type": "string",
                                "format": "date-time",
                                "example": "2017-11-06T19:38:09.890+11:00"
                            },
                            "specificWalletId": {
                                "description": "The ID of a specific wallet for which the payment is intended.  If present then the payment can only be used by the intended wallet.  If absent then any wallet can create a payment against the payment request",
                                "type": "string"
                            }
                        }
                    }
                ]
            },
            "customerPaymentRequestDetails": {
                "description": "Detailed information for a single payment request",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/commonPaymentBase"
                    },
                    {
                        "type": "object",
                        "required": [
                            "merchantId"
                        ],
                        "properties": {
                            "merchantId": {
                                "description": "The ID of the merchant associated with this transaction",
                                "type": "string"
                            },
                            "basket": {
                                "$ref": "#/components/schemas/basket"
                            }
                        }
                    }
                ]
            },
            "merchantPaymentDetails": {
                "description": "Detailed information for a single payment request",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/merchantPaymentSummary"
                    },
                    {
                        "type": "object",
                        "properties": {
                            "basket": {
                                "$ref": "#/components/schemas/basket"
                            },
                            "posPayload": {
                                "$ref": "#/components/schemas/posPayload"
                            },
                            "merchantPayload": {
                                "$ref": "#/components/schemas/merchantPayload"
                            }
                        }
                    }
                ]
            },
            "commonTransactionSummary": {
                "allOf": [
                    {
                        "type": "object",
                        "required": [
                            "transactionId",
                            "type",
                            "executionTime",
                            "status"
                        ],
                        "properties": {
                            "transactionId": {
                                "description": "The ID of the transaction",
                                "type": "string",
                                "example": "75ba5b0b-7e5d-47fe-9508-29ca69fdb1d5"
                            },
                            "clientReference": {
                                "description": "An optional client reference associated with the transaction.  If not supplied the transactionId will be used.",
                                "type": "string",
                                "example": "75ba5b0b-7e5d-47fe-9508-29ca69fdb1d5"
                            },
                            "type": {
                                "description": "The type of transaction: PAYMENT or REFUND",
                                "type": "string",
                                "enum": [
                                    "PAYMENT",
                                    "REFUND"
                                ]
                            },
                            "executionTime": {
                                "description": "Date/time stamp of when the transaction occurred in ISO string format",
                                "type": "string",
                                "format": "date-time",
                                "example": "2017-11-06T19:38:09.890+11:00"
                            },
                            "status": {
                                "description": "The current status of the transactions",
                                "type": "string",
                                "enum": [
                                    "PROCESSING",
                                    "APPROVED",
                                    "REJECTED"
                                ]
                            },
                            "statusDetail": {
                                "description": "The error detail returned by downstream processes when the payment is REJECTED",
                                "type": "object"
                            },
                            "refundReason": {
                                "description": "The reason provided for the refund.  Only provided for REFUND transactions",
                                "type": "string"
                            }
                        }
                    },
                    {
                        "$ref": "#/components/schemas/commonPaymentBase"
                    }
                ]
            },
            "customerTransactionSummary": {
                "description": "Summary information of the resulting transaction",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/commonTransactionSummary"
                    },
                    {
                        "type": "object",
                        "required": [
                            "merchantId",
                            "instruments"
                        ],
                        "properties": {
                            "merchantId": {
                                "description": "The ID of the merchant associated with this transaction",
                                "type": "string",
                                "example": "75ba5b0b-7e5d-47fe-9508-29ca69fdb1d5"
                            },
                            "instruments": {
                                "description": "The instruments used to make the payment.  For refunds and cash back amounts will be negative",
                                "type": "array",
                                "items": {
                                    "description": "An instrument used for this transaction",
                                    "type": "object",
                                    "required": [
                                        "paymentInstrumentId",
                                        "amount"
                                    ],
                                    "properties": {
                                        "paymentInstrumentId": {
                                            "description": "The ID of the payment instrument",
                                            "type": "string"
                                        },
                                        "amount": {
                                            "description": "The amount charged against or refunded to this instrument",
                                            "type": "number"
                                        },
                                        "paymentTransactionRef": {
                                            "description": "The reference for the payment",
                                            "type": "string"
                                        }
                                    }
                                }
                            }
                        }
                    }
                ]
            },
            "merchantTransactionSummary": {
                "description": "Summary information of the resulting transaction",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/commonTransactionSummary"
                    },
                    {
                        "type": "object",
                        "required": [
                            "walletId"
                        ],
                        "properties": {
                            "walletId": {
                                "description": "The ID of the wallet associated with this transaction",
                                "type": "string"
                            }
                        }
                    }
                ]
            },
            "customerTransactionDetail": {
                "description": "Detailed information for a single transaction",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/customerTransactionSummary"
                    },
                    {
                        "type": "object",
                        "properties": {
                            "basket": {
                                "$ref": "#/components/schemas/basket"
                            }
                        }
                    }
                ]
            },
            "merchantTransactionDetail": {
                "description": "Detailed information for a single transaction",
                "allOf": [
                    {
                        "$ref": "#/components/schemas/merchantTransactionSummary"
                    },
                    {
                        "type": "object",
                        "properties": {
                            "basket": {
                                "$ref": "#/components/schemas/basket"
                            },
                            "posPayload": {
                                "$ref": "#/components/schemas/posPayload"
                            },
                            "merchantPayload": {
                                "$ref": "#/components/schemas/merchantPayload"
                            }
                        }
                    }
                ]
            },
            "merchantSchemaSummary": {
                "type": "object",
                "required": [
                    "schemaId",
                    "type"
                ],
                "properties": {
                    "schemaId": {
                        "type": "string",
                        "description": "The unique ID assigned to the newly created schema"
                    },
                    "type": {
                        "type": "string",
                        "description": "The type of the schema e.g. pos, merchant"
                    },
                    "description": {
                        "type": "string",
                        "description": "A description for the schema"
                    },
                    "created": {
                        "type": "string",
                        "format": "date-time",
                        "example": "2017-11-06T19:38:09.890+11:00",
                        "description": "The ISO date/time when the schema was created"
                    }
                }
            },
            "paymentSession": {
                "description": "Mandatory data object containing response",
                "type": "object",
                "required": [
                    "location",
                    "paymentSessionId",
                    "merchantId",
                    "expiryTime",
                    "merchantInfo"
                ],
                "properties": {
                    "paymentSessionId": {
                        "description": "The ID of the payment session",
                        "type": "string"
                    },
                    "paymentRequestId": {
                        "description": "The ID of the associated payment request",
                        "type": "string"
                    },
                    "merchantId": {
                        "description": "The ID of the merchant initiating the payment session",
                        "type": "string"
                    },
                    "walletId": {
                        "description": "The ID of the customers wallet",
                        "type": "string"
                    },
                    "expiryTime": {
                        "description": "The ISO date/time for when the payment session will expire and become unusable",
                        "type": "string",
                        "format": "date-time",
                        "example": "2017-11-06T19:38:09.890+11:00"
                    },
                    "location": {
                        "description": "The location of the payment session (Used to group payment sessions)",
                        "type": "string"
                    },
                    "merchantInfo": {
                        "description": "Payload used to pass merchant information to the customer",
                        "$ref": "#/components/schemas/dynamicPayload"
                    },
                    "customerInfo": {
                        "description": "Payload used to pass customer information back to the merchant",
                        "$ref": "#/components/schemas/dynamicPayload"
                    }
                }
            },
            "dynamicPayload": {
                "type": "object",
                "properties": {
                    "schemaId": {
                        "description": "The ID of the previously configured schema that will be used to validate the contents of the payload",
                        "type": "string"
                    },
                    "payload": {
                        "description": "The payload aligned to the supplied schema",
                        "type": "object",
                        "additionalProperties": true
                    }
                }
            },
            "creditCard": {
                "required": [
                    "allowed",
                    "cardName",
                    "cardSuffix",
                    "cvvValidated",
                    "expired",
                    "expiryMonth",
                    "expiryYear",
                    "lastUpdated",
                    "paymentInstrumentId",
                    "paymentToken",
                    "primary",
                    "requiresCVV",
                    "scheme",
                    "status",
                    "stepUp",
                    "updateURL"
                ],
                "type": "object",
                "properties": {
                    "paymentInstrumentId": {
                        "description": "The credit card payment instrument id.",
                        "type": "string",
                        "example": "90731"
                    },
                    "paymentToken": {
                        "description": "The credit card payment token. The payment token is a unique identifier for the payment instrument.",
                        "type": "string",
                        "example": "712029a1-c0aa-41bc-a810-3d42424c5834"
                    },
                    "status": {
                        "description": "The status of the payment instrument in the container.",
                        "enum": [
                            "UNVERIFIED_PERSISTENT",
                            "VERIFIED"
                        ],
                        "type": "string"
                    },
                    "lastUpdated": {
                        "description": "The timestamp the payment instrument was last updated in the container. The timestamp format is ISO8601.",
                        "format": "date-time",
                        "type": "string",
                        "example": "2017-11-06T19:38:09.890+11:00"
                    },
                    "lastUsed": {
                        "description": "The timestamp the payment instrument was last used in the container. The timestamp format is ISO8601. Will be null if never used.",
                        "format": "date-time",
                        "type": "string",
                        "example": "2017-11-06T19:38:09.890+11:00"
                    },
                    "primary": {
                        "description": "A flag to indicate if this payment instrument is the primary instrument in the container.",
                        "type": "boolean",
                        "example": true
                    },
                    "allowed": {
                        "description": "A flag to indicate if the merchant profile in the container allows the use of this payment instrument.",
                        "type": "boolean",
                        "example": true
                    },
                    "expiryYear": {
                        "description": "The year of the expiry date of the credit card.",
                        "type": "string",
                        "example": "21"
                    },
                    "expiryMonth": {
                        "description": "The month of the expiry date of the credit card.",
                        "type": "string",
                        "example": "05"
                    },
                    "scheme": {
                        "description": "The credit card scheme.",
                        "type": "string",
                        "example": "MASTERCARD"
                    },
                    "cardSuffix": {
                        "description": "The suffix (last 4 digits) of the credit card number.",
                        "type": "string",
                        "example": "6106"
                    },
                    "cvvValidated": {
                        "description": "A flag to indicate if the CVV of the credit card has been validated.",
                        "type": "boolean",
                        "example": true
                    },
                    "cardName": {
                        "description": "The nickname of the credit card instrument in the container.",
                        "type": "string",
                        "example": "My Card"
                    },
                    "expired": {
                        "description": "A flag to indicate if the credit card is expired.",
                        "type": "boolean",
                        "example": true
                    },
                    "requiresCVV": {
                        "description": "A flag to indicate if payments with this credit card requires a CVV check.",
                        "type": "boolean",
                        "example": true
                    },
                    "updateURL": {
                        "description": "The URL of an iframe. This iframe is used to capture a credit card expiry and CVV.",
                        "type": "string",
                        "example": "https://uat.woolworthspay.com.au/container-ws/getCaptureFrame/cvvExpiry/353629ec-4cb5-4fc3-ab47-8c9c3f117ab8/90731"
                    },
                    "stepUp": {
                        "required": [
                            "mandatory",
                            "type",
                            "url"
                        ],
                        "type": "object",
                        "properties": {
                            "type": {
                                "description": "The type of the step up action. For credit cards this will be CAPTURE_CVV which identifies that the consumer must capture the CVV prior to payment.",
                                "type": "string",
                                "example": "CAPTURE_CVV"
                            },
                            "mandatory": {
                                "description": "A flag to indicate if this step up (action) is mandatory.",
                                "type": "boolean",
                                "example": true
                            },
                            "url": {
                                "description": "The URL of an iframe. This iframe is used to capture a credit card expiry and CVV or CVV only. The URL will automatically switch between expiry and CVV or CVV only endpoints based on the container requirement.",
                                "type": "string",
                                "example": "https://uat.woolworthspay.com.au/container-ws/getCaptureFrame/cvvExpiry/353629ec-4cb5-4fc3-ab47-8c9c3f117ab8/90731"
                            }
                        }
                    }
                }
            },
            "giftCard": {
                "required": [
                    "allowed",
                    "cardSuffix",
                    "lastUpdated",
                    "paymentInstrumentId",
                    "paymentToken",
                    "primary",
                    "programName",
                    "status"
                ],
                "type": "object",
                "properties": {
                    "paymentInstrumentId": {
                        "description": "The gift card payment instrument id.",
                        "type": "string",
                        "example": "81054"
                    },
                    "paymentToken": {
                        "description": "The gift card payment token. The payment token is a unique identifier for the payment instrument.",
                        "type": "string",
                        "example": "ec9b062a-220a-43b3-8185-a8ca4fc4dc0c"
                    },
                    "status": {
                        "description": "The status of the payment instrument in the container.",
                        "enum": [
                            "UNVERIFIED_PERSISTENT",
                            "VERIFIED"
                        ],
                        "type": "string"
                    },
                    "lastUpdated": {
                        "description": "The timestamp the payment instrument was last updated in the container. The timestamp format is ISO8601.",
                        "format": "date-time",
                        "type": "string",
                        "example": "2017-11-06T19:38:09.860+11:00"
                    },
                    "lastUsed": {
                        "description": "The timestamp the payment instrument was last used in the container. The timestamp format is ISO8601. Will be null if never used.",
                        "format": "date-time",
                        "type": "string",
                        "example": "2017-10-12T13:25:49.770+11:00"
                    },
                    "primary": {
                        "description": "A flag to indicate if this payment instrument is the primary instrument in the container.",
                        "type": "boolean",
                        "example": true
                    },
                    "allowed": {
                        "description": "A flag to indicate if the merchant profile in the container allows the use of this payment instrument.",
                        "type": "boolean",
                        "example": true
                    },
                    "programName": {
                        "description": "The gift card program name.",
                        "type": "string",
                        "example": "WISH Gift Card"
                    },
                    "cardSuffix": {
                        "description": "The suffix (last 4 digits) of the gift card number.",
                        "type": "string",
                        "example": "2517"
                    },
                    "stepUp": {
                        "required": [
                            "mandatory",
                            "type"
                        ],
                        "type": "object",
                        "properties": {
                            "type": {
                                "description": "The type of the step up action. For gift cards this will be REQUIRE_PASSCODE which identifies that the consumer must capture the PIN prior to payment.",
                                "type": "string",
                                "example": "REQUIRE_PASSCODE"
                            },
                            "mandatory": {
                                "description": "A flag to indicate if this step up (action) is mandatory.",
